{
    private string fileName;
    public void override Write(string message)
    {
        if(IsDiskFull)
        {
            throw new OutOfDiskSpaceException();
        }

        WriteToFile(fileName, message);
    }

    private bool IsDiskFull
    {
        get { return ...; }
    }
}

