// Excerpt from class under test
public class LoggerFactory
{
    private ILogger logger;
    public ILogger GetLogger()
    {
        if(!DoesLoggerExist)
        {
            logger = new RealLogger();
        }

        return logger;
    }

    private bool DoesLoggerExist
    {
        get { return logger != null; }
    }
}

